import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import axios from "axios";
import { Jumbotron } from "react-bootstrap";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright Â© "}
      Your Website and Your Name Here.
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
  root: {
    display: "flex"
  },
  toolbar: {
    paddingRight: 24 // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: "0 8px",
    ...theme.mixins.toolbar
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginRight: 36
  },
  menuButtonHidden: {
    display: "none"
  },
  title: {
    flexGrow: 1
  },
  drawerPaper: {
    position: "relative",
    whiteSpace: "nowrap",
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  drawerPaperClose: {
    overflowX: "hidden",
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up("sm")]: {
      width: theme.spacing(9)
    }
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: "100vh",
    overflow: "auto"
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4)
  },
  paper: {
    padding: theme.spacing(2),
    display: "flex",
    overflow: "auto",
    flexDirection: "column"
  },
  fixedHeight: {
    height: 240
  }
}));

export default function Main(props) {
  const classes = useStyles();
  const [empInfo, setEmpInfo] = React.useState({
    FullName: "",
    OfficeID: "",
    Department: ""
  });

  const [leaveInfo, setLeaveInfo] = React.useState({
    AvailLeaves: "",
    LeftOver: ""
  });

  useEffect(() => {
    if (localStorage.getItem("empTokken")) {
      setEmpInfo(localStorage.getItem("empID"));
    } else {
      props.history.push("/emplogin");
    }
    if (empInfo.FullName === "") {
      axios
        .post("/getemp", { ID: localStorage.getItem("empID") })
        .then(res => {
          setEmpInfo({
            OfficeID: res.data.OfficeID,
            FullName: res.data.FullName,
            Department: res.data.Department
          });
        })
        .catch(error => console.log(error));

      axios
        .post("/getleaveinfo", { ID: localStorage.getItem("empID") })
        .then(res => {
          setEmpInfo({
            OfficeID: res.data.OfficeID,
            FullName: res.data.FullName,
            Department: res.data.Department
          });
        })
        .catch(error => console.log(error));
    }
  });
  return (
    <main className={classes.content}>
      <div className={classes.appBarSpacer} />
      <Container maxWidth="lg" className={classes.container}>
        <Grid container spacing={3}>
          {/* User Details */}
          <Grid item xs={12} md={8} lg={9}>
            <Jumbotron>
              <h1 className="display-6">{"Name: " + empInfo.FullName}</h1>
              <p className="lead">Office ID: {empInfo.OfficeID}</p>
              <p className="lead">Department: {empInfo.Department}</p>
              <p className="lead">Available Leaves: {empInfo.AvailLeaves}</p>
              <p className="lead">Left Over Leaves: {empInfo.LeftOver}</p>

              <hr className="my-2" />
              <p></p>
              <p className="lead"></p>
            </Jumbotron>
          </Grid>
        </Grid>
      </Container>
      <Copyright />
    </main>
  );
}
