import React, { useState } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Box from "@material-ui/core/Box";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import { Link } from "react-router-dom";
import axios from "axios";
import desktopImage from "./img/homebackground.jpg";
import mobileImage from "./img/homebackground.jpg";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"Copyright © "}
      <Link to="/" style={{ textDecoration: "none" }}>
        Agile Leave Management System
      </Link>
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}
const imageUrl = window.innerWidth >= 650 ? desktopImage : mobileImage;

const useStyles2 = makeStyles(theme => ({
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.primary.main
  },
  bigAvatar: {
    width: 60,
    height: 60
  }
}));

const useStyles = makeStyles(theme => ({
  "@global": {
    body: {
      backgroundColor: "#c2dfff"
    }
  },
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.primary.main
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1)
  },
  submit: {
    margin: theme.spacing(3, 0, 2)
  }
}));

export default function Admin(props) {
  const classes2 = useStyles2();
  const classes = useStyles();
  const [adminInfo, setAdminInfo] = useState({ ID: "", Key: "" });

  const onClick = e => {
    e.preventDefault();
    axios
      .post("/adminlogin", adminInfo)
      .then(res => {
        localStorage.setItem("adminTokken", res.data.token);
        localStorage.setItem("adminID", adminInfo.ID);
        props.history.push("/admindashboard");
      })
      .catch(error => console.log(error));
    if (adminInfo.ID === "dummy" && adminInfo.Key === "dummy") {
      props.history.push("/admindashboard");
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes2.paper}>
        <Avatar
          alt="Remy Sharp"
          src="../img/login.png"
          className={classes2.bigAvatar}
        />
        <Typography
          component="h1"
          style={{
            color: "Black",
            fontSize: 35,
            textShadow:
              "-1px -1px 1px #aaa, 1px 5px 2px rgba(255,255,255), 5px 5px 6px rgba(255,255,250), 1px 1px 8px rgba(255,255,240)"
          }}
        >
          Admin Sign in
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="ID"
            label="ID"
            name="ID"
            autoComplete="ID"
            autoFocus
            onChange={e => setAdminInfo({ ...adminInfo, ID: e.target.value })}
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="Key"
            label="Key"
            type="Key"
            id="Key"
            autoComplete="current-Key"
            onChange={e => setAdminInfo({ ...adminInfo, Key: e.target.value })}
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          />
          <Button
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={onClick}
          >
            Sign In
          </Button>
        </form>
      </div>
      <Box mt={8}>
        <Copyright />
      </Box>
    </Container>
  );
}
