'use strict';

module.exports = {
  getSorted: function getSorted(array, key) {
    return array.slice().sort(function (a, b) {
      var aTech = a[key].substr(a[key].indexOf('('), a[key].indexOf(')'));
      var bTech = b[key].substr(b[key].indexOf('('), b[key].indexOf(')'));

      if (aTech > bTech) {
        return 1;
      } else if (aTech < bTech) {
        return -1;
      }

      return 0;
    });
  }
};